select.execute:

entries = <entries></entries>;
keywords = keyword.split(",");
entry = null;
for each (entry in keywords){
	searchQuery = 'select Album.Release.Image from music.track.search(1) where keyword="' + entry + '" LIMIT 1';
	albumArtQuery = y.query(searchQuery);
	albumArtURL = albumArtQuery.results.Track.Album.Release.Image.@url + '0';
	entries.appendChild(
		<album>{albumArtURL}</album>
	);
}
response.object = entries;
